import re

class Solution:
    def isSubsequence(self, s: str, t: str) -> bool:
        if s == "":
            return True
        sub_t = re.sub(r'[^{}]'.format(s),'', t)
        if s in sub_t:
            return True
        return False


if __name__ == '__main__':
    sol = Solution()

    s = "abc"
    t = "ahbgdc"
    assert sol.isSubsequence(s, t) == True


    s = "axc"
    t = "ahbgdc"
    assert sol.isSubsequence(s, t) == False

    s = "leeeeetcode"
    t = ""
    assert sol.isSubsequence(s, t) == True
